#include <bits/stdc++.h>using namespace std; int main() {    int testcases;    cin >> testcases;    while(testcases--){        int n;        cin >> n;        int A[n];        int i=0;        map<int,int> B;        for(i=0;i<n;i++){            cin >> A[i];            B[A[i]]++;        }        if(B.size()>2){            cout << "No" << "\n";            continue;        }        int a=0;        for(auto i:B){            int val=i.second;            if(val<n/2){                a=1;                break;            }        }        if(a==0){            cout << "Yes" << "\n";        }        else{            cout << "No" << "\n";        }    }}