#include <bits/stdc++.h>using namespace std; int main() {    int testcases;    cin >> testcases;    while(testcases--){        int a=0,n;        cin >> n;        int A[n];        int i=0;        for(i=0;i<n;i++){            cin >> A[i];        }        int b=A[0];        for(i=1;i<n-1;i++){            if(A[i-1]>A[i]){                a=1;                break;            }            else if(A[i-1]<A[i] && A[i]>A[i+1]){                if(b>A[i+1]){                    a=1;                    break;                }                swap(A[i],A[i+1]);            }        }        if(a==0){            cout << "YES" << "\n";        }        else{            cout << "NO" << "\n";        }    }}