#include <bits/stdc++.h>using namespace std; int main() {    int testcases;    cin >> testcases;    while(testcases--){        int n,distance;        cin >> n >> distance;        int A[n+1];        int i=0;        A[0]=0;        for(i=1;i<n+1;i++){            cin >> A[i];        }        int tank=0, curr=0;        for(i=1;i<=n;i++){            curr=A[i]-A[i-1];            tank=max(tank,curr);        }        curr=(distance-A[n])*2;         tank=max(tank,curr);        cout << tank << "\n";    }}