#include <bits/stdc++.h>using namespace std; int main() {    int testcases;    cin >> testcases;    while(testcases--){        int n,k;        cin >> n >> k;        int i=0;        int A[n];        for(i=0;i<n;i++){            cin >> A[i];        }        if(k==1){            int a=0;            for(i=1;i<n;i++){                if(A[i-1]>A[i])                {                    cout << "NO" << "\n";                    a=1;                    break;                }            }            if(a==0){                cout << "YES" << "\n";            }        }        else{            cout << "YES" << "\n";        }    }}