#include <bits/stdc++.h>using namespace std; int main() {    int a=0,i=0,n=0;    char b;    string s;    cin >> s;    n=s.size();    b=s[0];    int temp=0;    for(i=0;i<n;i++){        if(s[i]==b){            temp++;        }        else{            b=s[i];            temp=1;        }        a=max(a,temp);    }    if(a>=7){        cout << "YES";    }    else{        cout << "NO";    }}